import React, { useState } from 'react';

const AuthContext = React.createContext(
    {
        mealsArray: [],
        total_items: 0,
        addItem: (item) => {},
        removeItem: (item) => {}
    }
);

export const AuthContextProvider = (props) => {

    let mealsArray = [];
    const [total_items, setTotal] = useState(0);

    const addToCartHandler = mealitem => {
        mealsArray.forEach(meal => {
          if(meal.Name === mealitem.Name){
            meal.Amount += mealitem.Amount;
            mealitem.Amount = 0;
          }
        });
        if(mealitem.Amount !== 0) mealsArray.unshift(mealitem);
        setTotal(total_items + mealitem.Amount);
      }

    return <AuthContext.Provider value={{
        mealsArray: mealsArray,
        total_items: total_items,
        addItem: addToCartHandler,
        removeItem: (item) => {}
    }}>
        {props.children}
    </AuthContext.Provider>
}

export default AuthContext;
