import HeaderCartButton from './HeaderCartButton';
import classes from './Header.module.css';
import meals from './meals.jpg'
import AuthContext from '../../context-api';
import { useState } from 'react';

const Header = () => {

    const [clicked, setClicked] = useState(false);

    const cartHandler = (event) =>  setClicked(true);

    return <AuthContext.Provider value={{isClicked: clicked}}>
        <header className={classes.header}>
            <h2>Delicious Meals</h2>
            <HeaderCartButton onClick={cartHandler}/>
        </header>
        <div className={classes['main-image']}>
            <img src={meals} alt="Delicious meals"/>
        </div>
    </AuthContext.Provider>
}

export default Header;
