import { useContext } from 'react';
import AuthContext from '../../context-api';
import Card from '../UI/Card/Card';
import classes from './Cart.module.css';
import CartItem from './CartItem';

const Cart = () => {

    const ctx = useContext(AuthContext);

    return <Card className={classes['cart-items']}>
        {ctx.isClicked && <CartItem name={"Schnitzel"} amount={'1'} price={'20'} />}
        {ctx.isClicked && <div className={classes.total}>
            <span>Total Amount</span>
            <span>$20</span>
        </div>}
        {ctx.isClicked && <div className={classes.actions}>
            <button className={classes['button--alt']}>Cancel</button>
            <button className={classes['button']}>Order</button>
        </div>}
    </Card>
}

export default Cart;
