import Cart from '../Cart/Cart';
import classes from './Modal.module.css';
import ReactDOM from 'react-dom';
import Card from '../UI/Card/Card';

const ModalOverlay = (props) => {
    <>
        <div className={classes.backdrop} onClick={props.onCancel}></div>       
        <Card className={classes.modal}><Cart onCancel={props.onCancel}/></Card>
    </>
}

const Modal = (props) => {
    const portal = document.getElementById('modal-overlay');
    return <>
        {ReactDOM.createPortal(<ModalOverlay onCancel={props.onCancel}/>, portal)}
    </>
}
export default Modal;
