//import { DUMMY_MEALS as meals } from "./dummy-meals";
import MealItem from "./MealItem";
import classes from './Meals.module.css';
import Card from '../UI/Card/Card';
import { useContext, useEffect, useState } from "react";
import AuthContext from "../../context-api";

const Meals = () => {

    const url = 'https://react-api-96132-default-rtdb.firebaseio.com/meals/Meals.json';
    const [meals, setMeals] = useState([]);

    /*const fetchMeals = async() =>{
        try{
            const response = await fetch(url, {
                method: "GET"
            });
            if(!response.ok) throw new Error('Oops! Something happened');
            const temp = await response.json();
            setMeals(temp);
        }
        catch(e){
            console.log(e);
        }
    }*/

    useEffect(() => {
        async function fetchMeals(){
        try{
            const response = await fetch(url, {
                method: "GET"
            });
            if(!response.ok) throw new Error('Oops! Something happened');
            const temp = await response.json();
            setMeals(temp);
        }
        catch(e){
            console.log(e);
        }
        return fetchMeals;
    }},[meals]);

    const ctx = useContext(AuthContext);
    return <Card className={classes.meals}>
            {meals.map(meal => 
                <MealItem name={meal.name} desc={meal.description} price={meal.price} addMealItem={ctx.addItem} />)}
            </Card>
}
export default Meals;
