import { useContext } from 'react';
import AuthContext from '../../context-api';
import classes from './MealItem.module.css';
import MealItemForm from './MealItemForm';

const MealItem = (props) => {

    const price = props.price.toFixed(2);
    const ctx = useContext(AuthContext);

    const addMealItem = (amount) => {
        
        ctx.total_items += parseInt(amount, 10);
        ctx.mealsArray.forEach(meal => {
            if(meal.Name === props.name){
                amount += parseInt(meal.Amount, 10);
                ctx.mealsArray.splice(ctx.mealsArray.indexOf(meal),1,amount);
            }
        });
        props.addMealItem(
        {
            Name: props.name,
            Amount: amount,
            Price: price
        });
    }

    return <div className={classes.meal}>
            <section>
                <h3>{props.name}</h3>
                <div className={classes.description}>{props.desc}</div>
                <div className={classes.price}>${price}</div>  
            </section>    
                <MealItemForm onSave={addMealItem}/>
            </div>
}
export default MealItem;
