import React, { useState } from 'react';

const AuthContext = React.createContext(
    {
        mealsArray: [],
        addItem: (item) => {},
        removeItem: (item) => {}
    }
);

export const AuthContextProvider = (props) => {

    const [mealsArray, setArray] = useState([]);

    const addToCartHandler = mealitem => {
        mealsArray.forEach(meal => {
          if(meal.Name === mealitem.Name){
            meal.Amount += mealitem.Amount;
            mealitem.Amount = 0;
          }
        });
        if(mealitem.Amount !== 0) setArray(prev => [mealitem, ...prev]);
        else setArray(prev => prev);
      }

    return <AuthContext.Provider value={{
        mealsArray: mealsArray,
        addItem: addToCartHandler,
        removeItem: (item) => {}
    }}>
        {props.children}
    </AuthContext.Provider>
}

export default AuthContext;
