import { useContext, useState } from "react";
import Header from "./Components/Layout/Header";
import Modal from "./Components/Layout/Modal";
import Meals from "./Components/Meals/Meals";
import MealsSummary from "./Components/Meals/MealsSummary";
import AuthContext from "./context-api";

function App() {

  const [clicked, setClicked] = useState(false);

  const cartHandler = () => setClicked(!clicked);

  const ctx = useContext(AuthContext);
  const [array, setArray] = useState(ctx.mealsArray);

  const addToCartHandler = mealitem => {

    array.forEach(meal => {
      if(meal.Name === mealitem.Name){
        meal.Amount += mealitem.Amount;
        mealitem.Amount = 0;
      }
    });
    if(mealitem.Amount !== 0) setArray(array.concat(mealitem));
    ctx.mealsArray = array;
    cartHandler();
  }

  return (
    <>
      {clicked && <Modal onCancel={cartHandler}/>}
      <Header onClick={cartHandler}/>
      <MealsSummary />
      <Meals addMeal={addToCartHandler}/>
    </>
  );
}

export default App;
