import { useState} from "react";

const SimpleInput = () => {

  const [name, setName] = useState('');
  //const [isValid, setValidity] = useState(false);
  const [wasTouched, setTouch] = useState(false);
  const isValid = name.trim().length !== 0;


  const inputHandler = (e) => {
    setName(e.target.value);
    setTouch(true);
    //if(e.target.value.trim().length !== 0) setValidity(true);

  }

  const submitHandler = (e) => {
    e.preventDefault();
    setName('');
    setTouch(false);
    //checkValidity();
  }

  /*const checkValidity = () => {
    if(name.trim().length === 0) setValidity(false);
    else setValidity(true);
  }*/

  const blurHandler = () => {
    setTouch(true);
    //checkValidity();
  }

  const formValid = wasTouched && !isValid? 'form-control invalid' : 'form-control';

  return (
    <form onSubmit={submitHandler}>
      <div className={formValid}>
        <label htmlFor='name'>Your Name</label>
        <input 
          type='text' 
          id='name' 
          onChange={inputHandler}
          onBlur={blurHandler}
          value={name}
        />
      </div>
      { wasTouched && !isValid && <p className='error-text'>Name field should not be blank</p>}
      <div className="form-actions">
        <button>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
