import { useState} from "react";
import useInput from "../hooks/use-input";

const SimpleInput = () => {

  const [name, nameWasTouched, nameInputHandler, nameBlurHandler] = useInput();
  const [email, emailWasTouched, emailInputHandler, emailBlurHandler] = useInput();

  /*const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [nameWasTouched, setNameTouch] = useState(false);
  const [emailWasTouched, setEmailTouch] = useState(false);
  let formIsValid = false;

  const nameIsValid = name.trim().length !== 0;
  const emailIsValid = email.trim().includes('@');
  if(nameIsValid && emailIsValid) formIsValid = true;

  const nameInputHandler = (e) => setName(e.target.value);
  const emailInputHandler = (e) => setEmail(e.target.value);

  const nameBlurHandler = () => setNameTouch(true);
  const emailBlurHandler = () => setEmailTouch(true);

  const submitHandler = (e) => {
    e.preventDefault();
    setNameTouch(true);
    setEmailTouch(true);
    if(!nameIsValid || !emailIsValid) return;
    setName('');
    setEmail('');
    setNameTouch(false);
    setEmailTouch(false);
  }

  const nameFormValid = nameWasTouched && !nameIsValid ? 'form-control invalid' : 'form-control';
  const emailFormValid = emailWasTouched && !emailIsValid ? 'form-control invalid' : 'form-control';*/

  return (
    <form onSubmit={submitHandler}>
      <div className={nameFormValid}>
        <label htmlFor='name'>Name</label>
        <input 
          type='text' 
          id='name' 
          onChange={nameInputHandler}
          onBlur={nameBlurHandler}
          value={name}
        />
      </div>
      { nameWasTouched && !nameIsValid && <p className='error-text'>Name field should not be blank</p>}
      <div className={emailFormValid}>
        <label htmlFor='email'>Email</label>
        <input 
          type='text' 
          id='email' 
          onChange={emailInputHandler}
          onBlur={emailBlurHandler}
          value={email}
        />
      </div>
      { emailWasTouched && !emailIsValid && <p className='error-text'>Entered Email address is not valid</p>}
      <div className="form-actions">
        <button disabled={!formIsValid}>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
