import { useState} from "react";

const SimpleInput = () => {

  const [name, setName] = useState('');
  const [isValid, setValidity] = useState(null);
  const [wasTouched, setTouch] = useState(false);

  const touchHandler = () => setTouch(true);

  const inputHandler = (e) => setName(e.target.value);

  const submitHandler = (e) => {
    e.preventDefault();
    if(name.trim().length === 0) setValidity(false);
  }

  return (
    <form onSubmit={submitHandler}>
      <div className='form-control'>
        <label htmlFor='name'>Your Name</label>
        <input 
          type='text' 
          id='name' 
          onChange={inputHandler}
          value={name}
          onFocus={touchHandler}
        />
      </div>
      {!isValid && wasTouched && <p className='error-text'>Name field should not be blank</p>}
      <div className="form-actions">
        <button>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
