import useInput from '../hooks/use-input';

const BasicForm = () => {

  const {
    input: first,
    wasTouched: firstTouch,
    isValid: firstIsValid,
    inputHandler: firstInputHandler,
    blurHandler: firstBlur,
    reset: resetFirst
  } = useInput(value => value.trim() !== '');

  const {
    input: last,
    wasTouched: lastTouch,
    isValid: lastIsValid,
    inputHandler: lastInputHandler,
    blurHandler: lastBlur,
    reset: resetLast
  } = useInput(value => value.trim() !== '');

  const {
    input: email,
    wasTouched: emailTouch,
    isValid: emailIsValid,
    inputHandler: emailInputHandler,
    blurHandler: emailBlur,
    reset: resetEmail
  } = useInput(value => value.includes('@'));

  const submitHandler = (event) => {
    if(!emailIsValid || !firstIsValid || !lastIsValid) return;
    event.preventDefault();
    resetFirst();
    resetLast();
    resetEmail();
  }

  return (
    <form onSubmit={submitHandler}>
      <div className='control-group'>
        <div className='form-control'>
          <label htmlFor='name'>First Name</label>
          <input type='text' id='name' onChange={firstInputHandler} onBlur={firstBlur} value={first}/>
          {!firstIsValid && firstTouch && <p className='error-text'>Invalid First Name</p>}
        </div>
        <div className='form-control'>
          <label htmlFor='name'>Last Name</label>
          <input type='text' id='name' onChange={lastInputHandler} onBlur={lastBlur} value={last}/>
          {!lastIsValid && lastTouch && <p className='error-text'>Invalid Last Name</p>}
        </div>
      </div>
      <div className='form-control'>
        <label htmlFor='name'>E-Mail Address</label>
        <input type='text' id='name' onChange={emailInputHandler} onBlur={emailBlur} value={email}/>
        {!emailIsValid && emailTouch && <p className='error-text'>Invalid Email Address</p>}
      </div>
      <div className='form-actions'>
        <button>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
